mapper.select:
/* mInfo - mInfoList */
select M_NO, M_ID, M_PWD, M_NAME, M_GENDER, M_TEL, M_EMAIL, M_LEAVE, to_char(M_DATE, 'YYYY-MM-DD') as M_DATE 
from member

select M_NO, M_ID, M_PWD, M_NAME, M_GENDER, M_TEL, M_EMAIL, M_LEAVE, to_char(M_DATE, 'YYYY-MM-DD HH24:MI:SS') as M_DATE
from member 
where m_no = #{m_no}
