mapper.select:
select c.rnum, c.a_no, c.a_id, c.a_name, c.a_tel, c.a_email, c.po_name, c.pa_confirm, c.pa_date from (select rownum as rnum, b.a_no, b.a_id, b.a_name, b.a_tel, b.a_email, b.po_name, b.pa_confirm, b.pa_date from (select a_no, a_id, a_name, a_tel, a_email, po_name, p1.pa_confirm ,to_char(p1.pa_date, 'yyyy-MM-dd hh24:mm:ss') pa_date from ((select * from (select * from policy where po_type = '2' order by po_no desc) where rownum = 1) INNER join policy_agr p1 using(po_no)) inner join (select * from admin inner join policy_agr using(a_no)) USING(a_no)
a_id like '%'||#{adminSearch}||'%'
order by pa_date desc) b where rownum <= #{end_data}) c where c.rnum >= #{start_data}
