mapper.select:
/* product - productList */
select p_no, p_name, p_price, p_discount, color_code, size_code, p_fabric, p_caution, p_date, ca_no 
from product

/* product - productDetail */
select p_no, p_name, p_price, p_discount, color_code, size_code, p_fabric, p_caution, p_date, ca_no 
from product where p_no like #{p_no}

mapper.update:
/* product - productUpdate */
update product set p_name = #{p_name},
				   p_price = #{p_price},
				   p_discount = #{p_discount},
				   color_code = #{color_code},
				   size_code = #{size_code},
				   p_fabric = #{p_fabric},
				   p_caution = #{p_caution},
				   ca_no = #{ca_no}
where p_no = #{p_no}
