mapper.select:
/* product - productList */
select p_no, p_name, p_price, p_discount, color_code, size_code, p_fabric, p_caution, p_date, ca_no, su_no 
from product

/* product - productDetail */
select p_no, p_name, p_price, p_discount, color_code, size_code, p_fabric, p_caution, p_date, ca_no, su_no 
from product where p_no like #{p_no}
